package week1.day2;

public class HwEvenNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Write a java program to print even numbers between 100 to 200
		int i=0;
		for(i=100; i<=200; i++){
			if(i%2 == 0){
				System.out.println("This no is even :" +i);
			}
		}
	}

}
